import React, { useState } from "react";
import { IconPlus, IconMinus } from "@tabler/icons-react";
import { Accordion } from "@mantine/core";

const Order = () => {
  const groceries = [
    {
      emoji: "ðŸŽ",
      value: "Apples",
      description:
        "Crisp and refreshing fruit. Apples are known for their versatility and nutritional benefits. They come in a variety of flavors and are great for snacking, baking, or adding to salads.",
      subItems: [
        {
          emoji: "ðŸ",
          value: "Green Apples",
          description: "Green apples are known for their tart flavor and are great for salads and snacks.",
        },
        {
          emoji: "ðŸŽ",
          value: "Red Apples",
          description: "Red apples are sweet and can be enjoyed as a snack or in desserts.",
        },
      ],
    },
    {
      emoji: "ðŸŒ",
      value: "Bananas",
      description:
        "Naturally sweet and potassium-rich fruit. Bananas are a popular choice for their energy-boosting properties and can be enjoyed as a quick snack, added to smoothies, or used in baking.",
    },
    {
      emoji: "ðŸ¥¦",
      value: "Broccoli",
      description:
        "Nutrient-packed green vegetable. Broccoli is packed with vitamins, minerals, and fiber. It has a distinct flavor and can be enjoyed steamed, roasted, or added to stir-fries.",
    },
  ];

  const [openItems, setOpenItems] = useState([]);

  const toggleChevronIcon = (index) => {
    const isOpen = openItems.includes(index);
    setOpenItems((prevOpenItems) =>
      isOpen ? prevOpenItems.filter((item) => item !== index) : [...prevOpenItems, index]
    );
  };

  const renderSubItems = (subItems) => {
    return subItems.map((subItem, subIndex) => (
      <Accordion.Item key={subItem.value} value={subItem.value} open={openItems.includes(subIndex)}>
        <Accordion.Control
          icon={openItems.includes(subIndex) ? <IconMinus /> : <IconPlus />}
          onClick={() => toggleChevronIcon(subIndex)}
        >
          {subItem.value}
        </Accordion.Control>
        <Accordion.Panel>{subItem.description}</Accordion.Panel>
      </Accordion.Item>
    ));
  };

  const renderItems = () => {
    return groceries.map((item, index) => (
      <Accordion.Item key={item.value} value={item.value} open={openItems.includes(index)}>
        <Accordion.Control
          icon={openItems.includes(index) ? <IconMinus /> : <IconPlus />}
          onClick={() => toggleChevronIcon(index)}
        >
          {item.value}
        </Accordion.Control>
        <Accordion.Panel>{item.description}</Accordion.Panel>
        {item.subItems && renderSubItems(item.subItems)}
      </Accordion.Item>
    ));
  };

  return <Accordion>{renderItems()}</Accordion>;
};

export default Order;
