import React, { useState } from "react";
import { IconPlus, IconMinus } from "@tabler/icons-react";
import { Accordion } from "@mantine/core";

const Order = () => {
  const groceries = [
    {
      emoji: "ðŸŽ",
      value: "Apples",
      description:
        "Crisp and refreshing fruit. Apples are known for their versatility and nutritional benefits. They come in a variety of flavors and are great for snacking, baking, or adding to salads.",
    },
    {
      emoji: "ðŸŒ",
      value: "Bananas",
      description:
        "Naturally sweet and potassium-rich fruit. Bananas are a popular choice for their energy-boosting properties and can be enjoyed as a quick snack, added to smoothies, or used in baking.",
    },
    {
      emoji: "ðŸ¥¦",
      value: "Broccoli",
      description:
        "Nutrient-packed green vegetable. Broccoli is packed with vitamins, minerals, and fiber. It has a distinct flavor and can be enjoyed steamed, roasted, or added to stir-fries.",
    },
  ];

  // Use an object to store the open/closed state for each item
  const [itemStates, setItemStates] = useState({});

  const toggleChevronIcon = (value) => {
    setItemStates((prevStates) => {
      // Toggle the state for the specific item
      const newState = { ...prevStates, [value]: !prevStates[value] };
      return newState;
    });
  };

  const items = groceries.map((item) => (
    <Accordion.Item key={item.value} value={item.value}>
      <Accordion.Control
        icon={item.emoji}
        onClick={() => toggleChevronIcon(item.value)}
      >
        {item.value}
      </Accordion.Control>
      <Accordion.Panel>{item.description}</Accordion.Panel>
    </Accordion.Item>
  ));

  return (
    <Accordion chevronPosition="left" >
      {items.map((item) => (
        <Accordion.Item
          key={item.props.value}
          value={item.props.value}
          isOpen={itemStates[item.props.value]}
        >
          {React.cloneElement(item, {
            onClick: () => toggleChevronIcon(item.props.value),
          })}
        </Accordion.Item>
      ))}
    </Accordion>
  );
};

export default Order;
